"use client";

// REACTS
import Link from "next/link";
// import Image from "next/image";
// Antd Components
import { Carousel, Image } from "antd";

// Utils
import { formatPrice } from "@/utils/function/price";

// CODE
export default function ServiceCard({ item }: { item: any }) {
  return (
    <Link href={`/services/${item.id}`} className="block text-center group">
      <div key={item.id} className="text-center">
        <div className="aspect-square relative mb-4 overflow-hidden">
          {item.images && item?.images?.length > 1 ? (
            <Carousel
              arrows
              dots
              dotPosition="bottom"
              className="w-full h-full"
              effect="fade"
              autoplay
            >
              {item.images.map((img: string, index: number) => (
                <div
                  key={index}
                  className="flex justify-center items-center bg-white"
                >
                  <Image
                    src={img}
                    alt={`${item.service_name}-${index}`}
                    className="object-cover group-hover:scale-105 transition-transform duration-500 "
                    height={300}
                    preview={true}
                    key={index}
                    style={{ aspectRatio: "1/1" }}
                  />
                </div>
              ))}
            </Carousel>
          ) : (
            <Image
              src={item?.image || "eror"}
              alt={item?.service_name}
              className="object-cover w-full h-full group-hover:scale-105 transition-transform duration-500 bg-gray-200"
              height={300}
              fallback="data:image/png;base64,"
              preview={false}
            />
          )}
        </div>

        <h3 className="text-xl font-semibold text-black mb-2 cursor-pointer hover:text-blue-600 transition-colors">
          {item.service_name}
        </h3>

        <div className="text-gray-500">
          {item?.discount && (
            <span className="line-through mr-2">
              {formatPrice(item?.price)}
            </span>
          )}
          <span className="font-semibold">
            {item.discount
              ? formatPrice(item?.price - item.discount)
              : formatPrice(item.price)}
          </span>
        </div>
      </div>
    </Link>
  );
}
